import { useEffect, useState } from 'react';
import { ethers } from 'ethers'; // connects project to the blockchain

// Components
import Navigation from './components/Navigation';
import Search from './components/Search';
import Home from './components/Home';

// ABIs
import RealEstate from './abis/RealEstate.json'
import Escrow from './abis/Escrow.json'

// Config
import config from './config.json';

function App() {

  const [account, setAccount] = useState(null) // read and set state of account

  const loadBLockchainData = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum) // allows metamask window
    console.log(provider)
    const accounts = await window.ethereum.request({method: 'eth_requestAccounts'})
    setAccount(accounts[0]) // grab the metamask account
    console.log(accounts[0])
  }

  useEffect(() => {
    loadBLockchainData() // calls blockchain loader
  }, [])

  return (
    <div>

      <div className='cards__section'>

        <h3>Welcome to Millow</h3>

      </div>

    </div>
  );
}

export default App;
