import { useEffect, useState } from 'react';
import { ethers } from 'ethers'; // connects project to the blockchain

// Components
import Navigation from './components/Navigation';
import Search from './components/Search';
import Home from './components/Home';

// ABIs
import RealEstate from './abis/RealEstate.json'
import Escrow from './abis/Escrow.json'

// Config
import config from './config.json';

function App() {

  const [provider, setProvider] = useState(null) // allow multiple wallets

  const [account, setAccount] = useState(null) // read and set state of metamask account

  const loadBLockchainData = async () => {
    const provider = new ethers.providers.Web3Provider(window.ethereum) // allows metamask window
    setProvider(provider)
    console.log(provider)
  
    window.ethereum.on('accountsChanged', async () => { // when user changes their account in metamask
      const accounts = await window.ethereum.request({method: 'eth_requestAccounts'}) // get the account list
      const account = ethers.utils.getAddress(accounts[0]) // grab the current metamask account
      setAccount(account) // set the metamask account
      console.log(account)
    })
  }

  useEffect(() => {
    loadBLockchainData() // calls blockchain loader
  }, [])

  return (
    <div>

      <Navigation account={account} setAccount={setAccount} />

      <Search />

      <div className='cards__section'>

        <h3>Homes for you</h3>

        <hr />

        <div className='cards'>
          <div className = 'card'>
            <div className = 'card__image'>
              <img src="" alt="Home"/>
            </div>
            <div className='card__info'>
              <h4>1 ETH</h4>
              <p>
                <strong>1</strong> beds |
                <strong>2</strong> bath |
                <strong>3</strong> sqft
              </p>
              <p>1234 Elm Street</p>
            </div>
          </div>
        </div>
      </div>

    </div>
  );
}

export default App;
