import { ethers } from 'ethers';
import { useEffect, useState } from 'react';

import close from '../assets/close.svg';

/*
var hasBought = false
const buyHandler = () => { // button for new page
    hasBought = true
  }
*/

const Home = ({ realty, provider, account, escrow, togglePopup }) => {

    // set React component states
    const [buyer, setBuyer] = useState(null)
    const [lender, setLender] = useState(null)
    const [inspector, setInspector] = useState(null)
    const [seller, setSeller] = useState(null)

    const [hasBought, setHasBought] = useState(null)
    const [hasLended, setHasLended] = useState(null)
    const [hasInspected, setHasInspected] = useState(null)
    const [hasSold, setHasSold] = useState(null)

    const [owner, setOwner] = useState(null)

    const fetchDetails = async () => { // get contract details
        const buyer = await escrow.buyer(realty.id) // returns eth address of the buyer from escrow .js object
        setBuyer(buyer)

        const lender = await escrow.lender() // returns eth address of the lender from escrow .js object
        setLender(lender)

        const inspector = await escrow.inspector() // returns eth address of the inspector from escrow .js object
        setInspector(inspector)

        const seller = await escrow.seller() // returns eth address of the seller from escrow .js object
        setSeller(seller)

        const hasBought = await escrow.approval(realty.id, buyer) // returns contract approval of the buyer from escrow .js object
        setHasBought(hasBought)

        const hasLended = await escrow.approved(realty.id, lender) // returns contract approval of the lender from escrow .js object
        setHasLended(hasLended)

        const hasInspected = await escrow.inspectionPassed(realty.id) // returns contract approval of the inspector from escrow .js object
        setHasInspected(hasInspected)

        const hasSold = await escrow.isListed(realty.id, seller) // returns contract approval of the seller from escrow .js object
        setHasSold(hasSold)
    }

    const fetchOwner = async () => { // get contract owner

        if (await escrow.isListed(realty.id)) return // if it is listed
            const owner = await escrow.buyer(realty.id) // make the buyer the owner 
        setOwner(owner)
    }

    useEffect(() => { // react check for changes once sold
        fetchDetails()
        fetchOwner()
    }, [hasSold])

    return (
        <div className="home" onClick={() => togglePopup(realty)}>
            <div className="home__details">

                <div className="home__image" style={{"grid-area": "home__image"}}>
                    <img src={realty.image} alt="Home" />
                </div>

                <div className="home__overview" style={{"grid-area": "home__overview", "overflow-y": "clip"}}>
                    <h1>{realty.name}</h1>
                    <p>
                        <strong>{realty.attributes[2].value}</strong> beds |
                        <strong>{realty.attributes[3].value}</strong> bath |
                        <strong>{realty.attributes[4].value}</strong> sqft
                    </p>
                    <p>{realty.address}</p>
                    
                </div>

                <div style={{"grid-area": "home__buy","margin": "auto","height":"100px"}}>
                    <h2 style={{"text-align": "center"}}>{realty.attributes[0].value} ETH</h2>
                    
                    {/* If it has an owner */}
                    {owner ? ( 
                        <div className='home__owned'>
                            Owned by {owner.slice(0, 6) + '...' + owner.slice(38, 42)}
                        </div>
                        
                    ) : (
                      {/*  
                
                            (account === inspector) ? (
                                <button className="home__buy" onClick={false} disabled={hasBought}>
                                    Approve Inspection
                                </button>
                            ) : (account === lender) ? (
                                <button className="home__buy" onClick={false} disabled={hasBought}>
                                    Approve & Lend
                                </button>
                            ) : (account === seller) ? (
                                <button className="home__buy" onClick={false} disabled={hasBought}>
                                     Approve & Sell
                                </button>
                            ) : (
                                <button className="home__buy" onClick={false} disabled={hasBought}>
                                     Buy
                                </button>
                            )
                       */} 
                    )}
                    
                </div>



                

                <div style={{"grid-area": "home__contact","margin": "auto","height":"80px"}}>
                    <button className="home__contact" onClick={false} disabled={hasBought}>
                        Contact Agent
                    </button>
                </div>

                <div style={{"grid-area": "home__description"}}>
                    <hr />
                    <h2>Overview</h2>
                    <p style={{"margin-left": "10px","margin-top":"5px"}}>{realty.description}</p>
                </div>

                <div style={{"grid-area": "home__features"}}>
                    <hr />
                    <h2>Facts and Features</h2>
                    <ul style={{"margin-left": "30px"}}>
                        {realty.attributes.map((attribute, index) => (
                            <li key={index}><strong>{attribute.trait_type}</strong> : {attribute.value}</li>
                        ))}
                    </ul>
                </div>

                <button className ="home__close"></button>
            </div>
        </div>
    );
}

export default Home;
